<?phpclass FollowerTrigger extends MF_ActionTriggerAbstract{		public function __construct( Follower $model ){		parent::__construct( $model );	}		public function afterInsert(){		$data = array(			'mentioned_user_id' => $this->model->users2_id		);		MF_FeedNotification::sendFeed( $this->model->users1_id, $data, 1);				$data = array(			'mentioned_user_id' => $this->model->users1_id		);		$user2 = $this->model->getParent( 'User', 'user2' );		MF_FeedNotification::sendNotification( $user2, $data, 1);			}	}